package com.banmenit.demo.mqtt;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;/** * 功能说明： 配置所有的工具类； * * @ com.ubtechinc.util * <p> * Original @Author: deane.jia-贾亮亮,@2020/7/30@16:26 * <p> * Copyright (C)2012-@2020 深圳优必选科技 All rights reserved. */@Componentpublic class PropertyUtils {    public static String mqttUserName;    public static String mqttPassword;    public static String shareTopic;    public static String clientPrefix;    public static String url;    public static Integer clientId;    public static int corePoolSize;    public static int maxPoolSize;    @Value(value = "${spring.mqtt.userName}")    public void setMqttUserName(String mqttUserName) {        PropertyUtils.mqttUserName = mqttUserName;    }    @Value(value = "${spring.mqtt.password}")    public void setMqttPassword(String mqttPassword) {        PropertyUtils.mqttPassword = mqttPassword;    }    @Value(value = "${spring.mqtt.inbound.topics}")    public void setShareTopic(String shareTopic) {        PropertyUtils.shareTopic = shareTopic;    }    @Value(value = "${spring.mqtt.inbound.clientId}")    public void setClientPrefix(String clientPrefix) {        PropertyUtils.clientPrefix = clientPrefix;    }    @Value(value = "${spring.mqtt.url}")    public void setUrl(String url) {        PropertyUtils.url = url;    }    @Value("#{T(java.lang.Integer).parseInt('${spring.mqtt.client.id:10000}')}")    public void setClientId(Integer clientId) {        PropertyUtils.clientId = clientId;    }    @Value(value="${spring.mqtt.pool.core.size:8}")    public void setCorePoolSize(String corePoolSize) {        PropertyUtils.corePoolSize = Integer.parseInt(corePoolSize);    }    @Value(value="${spring.mqtt.pool.max.size:16}")    public void setMaxPoolSize(String maxPoolSize) {        PropertyUtils.maxPoolSize = Integer.parseInt(maxPoolSize);    }}